import React from "react";
import Navbar from "../Components/Navbar/Navbar";
import { NewsContext } from "../NewsContext";
import { Link, useParams } from "react-router-dom";
import "./article.css";

const ArticleOpen = () => {
  const params = useParams();

  const { value1, value2, value3, value4, value5, value6 } =
    React.useContext(NewsContext);
  const [generalCards] = value1;
  const [sportCards] = value2;
  const [techCards] = value3;
  const [buisnessCards] = value4;
  const [healthCards, sethealthCards] = value5;
  const [entertainCards, setentertainCards] = value6;

  const showdata = (category) => {
    console.log(entertainCards[params.index]);
    console.log(category);
    if (category === "general") {
      return (
        <div className="container wrapper">
          <img
            className="responsiv-img"
            alt=""
            src={generalCards[params.index].urlToImage}
          ></img>
          <h1 className="title">{generalCards[params.index].title}</h1>
          <div className="meta-info">
            <p>
              The Vikings needed a last-second stop to secure their sixth win of
              the season
            </p>
          </div>
          <h1 className="date-right">{generalCards[params.index].author}</h1>

          <p className="entry-content">
            he Vikings nearly delivered a knockout before having to hold off a
            furious comeback attempt by the visiting Steelers on Thursday night.
            Down 29-0, the Steelers scored 28 of the game's next 35 pointsâ€¦{" "}
            <Link
              to="https://www.cbssports.com/nfl/news/vikings-vs-steelers-score-live-updates-results-game-stats-highlights-for-thursday-night-football/live/"
              className="url"
            >
              READ FULL ARTICLE
            </Link>
          </p>

          <h3 className="date">{generalCards[params.index].author}</h3>
          <h3 className="author">Bryan DeArdo</h3>
        </div>
      );
    }
    if (category === "sports") {
      return (
        <div className="container wrapper">
          <img
            className="responsiv-img"
            alt=""
            src={sportCards[params.index].urlToImage}
          ></img>
          <h1 className="title">{sportCards[params.index].title}</h1>
          <div className="meta-info">
            <p>
              The Vikings needed a last-second stop to secure their sixth win of
              the season
            </p>
          </div>
          <h1 className="date-right">{sportCards[params.index].author}</h1>

          <p className="entry-content">
            <Link
              to="https://www.cbssports.com/nfl/news/vikings-vs-steelers-score-live-updates-results-game-stats-highlights-for-thursday-night-football/live/"
              className="url"
            >
              READ FULL ARTICLE
            </Link>
          </p>

          <h3 className="date">{sportCards[params.index].author}</h3>
          <h3 className="author">Bryan DeArdo</h3>
        </div>
      );
    }
    if (category === "entertainment") {
      return (
        <div className="container wrapper">
          <img
            className="responsiv-img"
            alt=""
            src={entertainCards[params.index].urlToImage}
          ></img>
          <h1 className="title">{entertainCards[params.index].title}</h1>
          <div className="meta-info">
            <p>
              The Vikings needed a last-second stop to secure their sixth win of
              the season
            </p>
          </div>
          <h1 className="date-right">{entertainCards[params.index].author}</h1>

          <p className="entry-content">
            <Link
              to="https://www.cbssports.com/nfl/news/vikings-vs-steelers-score-live-updates-results-game-stats-highlights-for-thursday-night-football/live/"
              className="url"
            >
              READ FULL ARTICLE
            </Link>
          </p>

          <h3 className="date">{entertainCards[params.index].author}</h3>
          <h3 className="author">Bryan DeArdo</h3>
        </div>
      );
    }
    if (category === "technology") {
      return (
        <div className="container wrapper">
          <img
            className="responsiv-img"
            alt=""
            src={techCards[params.index].urlToImage}
          ></img>
          <h1 className="title">{techCards[params.index].title}</h1>
          <div className="meta-info">
            <p>
              The Vikings needed a last-second stop to secure their sixth win of
              the season
            </p>
          </div>
          <h1 className="date-right">{techCards[params.index].author}</h1>

          <p className="entry-content">
            <Link
              to="https://www.cbssports.com/nfl/news/vikings-vs-steelers-score-live-updates-results-game-stats-highlights-for-thursday-night-football/live/"
              className="url"
            >
              READ FULL ARTICLE
            </Link>
          </p>

          <h3 className="date">{techCards[params.index].author}</h3>
          <h3 className="author">Bryan DeArdo</h3>
        </div>
      );
    }
    if (category === "buisness") {
      return (
        <div className="container wrapper">
          <img
            className="responsiv-img"
            alt=""
            src={buisnessCards[params.index].urlToImage}
          ></img>
          <h1 className="title">{buisnessCards[params.index].title}</h1>
          <div className="meta-info">
            <p>
              The Vikings needed a last-second stop to secure their sixth win of
              the season
            </p>
          </div>
          <h1 className="date-right">{buisnessCards[params.index].author}</h1>

          <p className="entry-content">
            <Link
              to="https://www.cbssports.com/nfl/news/vikings-vs-steelers-score-live-updates-results-game-stats-highlights-for-thursday-night-football/live/"
              className="url"
            >
              READ FULL ARTICLE
            </Link>
          </p>

          <h3 className="date">{buisnessCards[params.index].author}</h3>
          <h3 className="author">Bryan DeArdo</h3>
        </div>
      );
    }
    if (category === "health") {
      return (
        <div className="container wrapper">
          <img
            className="responsiv-img"
            alt=""
            src={healthCards[params.index].urlToImage}
          ></img>
          <h1 className="title">{healthCards[params.index].title}</h1>
          <div className="meta-info">
            <p>
              The Vikings needed a last-second stop to secure their sixth win of
              the season
            </p>
          </div>
          <h1 className="date-right">{healthCards[params.index].author}</h1>

          <p className="entry-content">
            <Link
              to="https://www.cbssports.com/nfl/news/vikings-vs-steelers-score-live-updates-results-game-stats-highlights-for-thursday-night-football/live/"
              className="url"
            >
              READ FULL ARTICLE
            </Link>
          </p>

          <h3 className="date">{healthCards[params.index].author}</h3>
          <h3 className="author">Bryan DeArdo</h3>
        </div>
      );
    }
  };

  return (
    <div>
      <Navbar />
      {showdata(params.categoryName)}
    </div>
  );
};

export default ArticleOpen;
